import React, { useEffect, useState } from "react";
import { instance } from "../../axios/axios";
import { Link } from "react-router-dom";
import { Button, Container, Form, Modal, Row, Col,ButtonGroup,Image } from "react-bootstrap";
import "./ViewProduct.css";
import StarBorder from "@material-ui/icons/StarBorder";
function ViewProduct(props) {
  const id = props.id;
  const [show, setshow] = useState(false);
  const [quantity, setquantity] = useState(1);
  const [product, setproduct] = useState([]);
  const [main, setmain] = useState("");
  const [size, setsize] = useState("")
  const [err, seterr] = useState("")
  const [similar, setsimilar] = useState([])
  const addSize=(e)=>{
    seterr("")
    console.log(e.target.value)
    setsize(e.target.value)
  }
  const increaseCount=()=>{
    setquantity(prev=>(prev+1))
  }
  const decreaseCount=()=>{
    if(quantity>1) 
    {setquantity(prev=>(prev-1))}else{
      setquantity(1)
    }
    
  }
  const handleClose =()=> setshow(false)
  const addToBag = async (id,price,offer) => {
    console.log("clicked")
    if(!size) return seterr("Please select one size")
    const data={
      id:id,
      orderStatus:"Placed",
      size:size,
      quantity:quantity,
      price:price,
      offer:offer
    }
    console.log(data)
    await instance.post("/addtocart/",data).then((response) => {
      if (response.data.status===false) return seterr("Cart already exist")
        setshow(true);
      
    });
  };
  const getProduct = async () => {
    const datas = await instance.get(`/getviewproduct/${id}`);
    if (datas) {
      setproduct(datas.data);
      setmain(datas.data[0].url[0].image1);
      const value=datas.data[0].subcategory
      const similars=await instance.get(`/similarproduct/${value}`)
      if(similars.data){
        console.log(similars.data)
        setsimilar(similars.data)
      }
    }
  };
  useEffect(() => {
    getProduct();
  }, [id]);
  return (
    <div className="view_product_page">
      <div className="view_product_sub_container">
        <div className="view_product_image">
          <div className="small_image">
            {product.map((product) => {
              return (
                <>
                  <img
                    className="small_image_view"
                    src={product.url[0].image1}
                    alt=""
                    onClick={() => {
                      setmain(product.url[0].image1);
                    }}
                  />
                  <img
                    className="small_image_view"
                    src={product.url[0].image2}
                    alt=""
                    onClick={() => {
                      setmain(product.url[0].image2);
                    }}
                  />
                  <img
                    className="small_image_view"
                    src={product.url[0].image3}
                    alt=""
                    onClick={() => {
                      setmain(product.url[0].image3);
                    }}
                  />
                  <img
                    className="small_image_view"
                    src={product.url[0].image4}
                    alt=""
                    onClick={() => {
                      setmain(product.url[0].image4);
                    }}
                  />
                </>
              );
            })}
          </div>
          <div className="large_image">
            {product.map((product) => {
              return (
                <>
                  <img src={main} alt="" />
                 
                  <Button className="buy_now_button mt-4">Buy now</Button>
                  <p style={{ color: "green" }}>
                    {product.deliverymethod === "Free"
                      ? "Free delivery"
                      : "Free delivery not available"}
                  </p>
                 
                </>
              );
            })}
          </div>
        </div>
        {product.map((content) => {
          return (
            <>
              <div className="content">
                <div className="descrriptions">
                  <h4>{content.productname}</h4>
                  <p>{content.description}</p>
                  <div className="check__if__offer__container">
                  <p className="view__page__para">Price : Rs.{content.price}</p>
                  {!content.offer ? <p style={{ color: "blue" }}>Rs.{content.price}</p>:<p style={{ color: "blue" }}>Rs.{Math. trunc(content.price-content.offer)}</p>}
                

                  </div>


                  

                  <Form>
                    <Form.Select
                      aria-label="Default select example"
                      className="mb-3 size__select__option"
                      onChange={addSize}
                    >
                      <option  selected>select size :</option>

                      <option onChange={addSize}>small</option>
                      <option onChange={addSize}>medium</option>
                      <option onChange={addSize}>large</option>
                    </Form.Select>
                  </Form>
                  <ButtonGroup aria-label="Basic example">
                        <Button variant="secondary" onClick={increaseCount}>+</Button>
                        <Button variant="secondary">{quantity}</Button>
                        <Button variant="secondary" onClick={decreaseCount}>-</Button>
                      </ButtonGroup>
                  <p className="stock_left">Only {content.small+content.medium+content.large} left</p>
                  <Button
                    className="add_to_bag_button "
                    onClick={() => {
                      addToBag(content._id,content.price,content.offer);
                    }}
                  >
                    Add to bag
                    </Button>
                    {err && <p style={{position:"absolute"}}>{err}</p>}
                 
                </div>
               
              </div>
              
            </>
          );
        })}
       
        
      </div>
      <div className="second__container__view__products">
        <Container>
         <Row className="second__container__view__products__row">
           <Col mad={6}>
             <div className="review_container">
             <h3>View reviews</h3>
             </div>
             <div className="image_review_profile">
               <div className="review__box">
<Image className="review__pic" src="data:image/png;base64," roundedCircle />   
             <p>It was a nice product and i really liked it</p>
               </div>
               
             <div>
                              <StarBorder/>
                              <StarBorder/>
                              <StarBorder/>
                              <StarBorder/>
                              <StarBorder/>
                            
                          </div>
             </div>
             <div className="image_review_profile">
               <div className="review__box">
<Image className="review__pic" src="data:image/png;base64," roundedCircle />   
             <p>It was a nice product and i really liked it</p>
               </div>
               
             <div>
                              <StarBorder/>
                              <StarBorder/>
                              <StarBorder/>
                              <StarBorder/>
                              <StarBorder/>
                            
                          </div>
             </div>
            
            
             
            
            
            
           </Col>
           <Col mad={6}>
             <div className="head__similar__products">
             <h3>Similar  products</h3>

             </div>
             <div className="head__similar__products">
               {similar.map((product)=>{
                 return(<>
                 <Link className="link_subcategory" style={{textDecoration:"none"}} to={`/viewproduct/${product._id}`}>
<img className="similar__pic" src={product.url[0].image1} alt="" /></Link>

</>
                 )
               })}

            

             </div>
            
            
          
         </Col>
         </Row>
         </Container>
      </div>
     
      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Your product added to bag successfully</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="container">
            {product.map((product) => {
              return (
                <>
                  <Container>
                    <Row>
                      <Col md={6}>
                        <img
                          className="small_image_view"
                          src={product.url[0].image1}
                          alt="image"
                        />
                        <p>{product.productname}</p>
                      </Col>
                      <Col md={6}>
                        
                        <p>Total price : {product.price*quantity}</p>
                      </Col>
                    </Row>
                  </Container>
                </>
              );
            })}
          </div>
         <Link to="/addtobag"> <Button>Go to bag</Button></Link>
        </Modal.Body>
      </Modal>
    
    </div>
    
  );
}

export default ViewProduct;
